$color-primary-1: #F56E6E;
$color-primary-2: #D64141;
$color-primary-3: #970A0A;
$color-primary-4: #6E0000;

$color-secondary-1-0: #B89E20;	// Main Secondary color (1) */
$color-secondary-1-1: #F5DE6E;
$color-secondary-1-2: #D6BC41;
$color-secondary-1-3: #977F0A;
$color-secondary-1-4: #6E5B00;

$color-secondary-2-0: #38227F;	// Main Secondary color (2) */
$color-secondary-2-1: #6A57A9;
$color-secondary-2-2: #4E3893;
$color-secondary-2-3: #261268;
$color-secondary-2-4: #17074B;

$color-complement-0: #199419;	// Main Complement color */
$color-complement-1: #58C458;
$color-complement-2: #34AB34;
$color-complement-3: #087908;
$color-complement-4: #005800;



// As RGBa codes */

$rgba-primary-0: rgba(184, 32, 32,1);	// Main Primary color */
$rgba-primary-1: rgba(245,110,110,1);
$rgba-primary-2: rgba(214, 65, 65,1);
$rgba-primary-3: rgba(151, 10, 10,1);
$rgba-primary-4: rgba(110,  0,  0,1);

$rgba-secondary-1-0: rgba(184,158, 32,1);	// Main Secondary color (1) */
$rgba-secondary-1-1: rgba(245,222,110,1);
$rgba-secondary-1-2: rgba(214,188, 65,1);
$rgba-secondary-1-3: rgba(151,127, 10,1);
