  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: fixed;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, .9);
  visibility: hidden;
  z-index: 1;
  cursor: pointer;
  animation-name: overlay-expand;
  animation-duration: 700ms;

  &.visible {
    visibility: visible;
  }

  &.victory.visible {
    
    h1 {
      color: $color-complement-1;
      visibility: visible;
    }
  }

  &.defeat.visible {

