  h1, p {
    font-family: 'Piedra', cursive;
  }

  h1 {
    font-size: 3rem;
    text-align: center;
    color: $color-secondary-2-1;
  }

  p {
    font-size: 1.2rem;
    color: $color-secondary-1-2;
    margin: 1rem;
